import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

export default function App() {
  return (
    <View style={styles.container}>
      <LinearGradient
        colors={['#C7F4F6', '#D1F4F6', '#E5F4F5', '#00CCF9']}
        locations={[0, 0.3, 0.85, 1]} // Vị trí của mỗi màu trong gradient (từ 0 đến 1)
        style={styles.background}
      />
      {/* Các phần còn lại của ứng dụng */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  background: {
    ...StyleSheet.absoluteFillObject, // Để gradient chiếm toàn bộ không gian của màn hình
  },
  // Các kiểu CSS khác cho các thành phần khác của ứng dụng
});
