import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

export default function App() {
  return (
    <View style={styles.container}>
      <LinearGradient
        colors={['#C7F4F6', '#D1F4F6', '#E5F4F5', '#00CCF9']}
        locations={[0, 0.3, 0.85, 1]} // Vị trí của mỗi màu trong gradient (từ 0 đến 1)
        style={styles.background}
      />
      {/* Các phần còn lại của ứng dụng */}
      <View style={styles.view0}></View>

      <View style={styles.view1}>
        <View style={styles.view1.circle}></View>
      </View>

      <View style={styles.view2}>
        <Text style={styles.view2.title}>GROW {"\n"}YOUR BUSINESS</Text>
      </View>

      <View style={styles.view3}>
        <Text style={styles.view3.title}>We will help you to grow your business using {"\n"}online server</Text>
      </View>

      <View style={styles.view4}>
        <Button title='LOGIN' color='#E3C000' >
        </Button>
        <Button title='SIGN UP' color='#E3C000' >

        </Button>
      </View>
      
      <LinearGradient
        // Background Linear Gradient
        colors={['rgba(0,0,0,0.8)', 'transparent']}
        style={styles.background}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  background: {
    ...StyleSheet.absoluteFillObject, // Để gradient chiếm toàn bộ không gian của màn hình
  },
  // Các kiểu CSS khác cho các thành phần khác của ứng dụng
   view1: {
    flex: 1,
    circle: {
      width: 140,
      height: 140,
      borderRadius: 9999,
      borderWidth: 15,
      borderColor: 'black',
      backgroundColor: 'rgba(196, 196, 196, 0)',
      flex: 1,
      justifyContent: 'center',
      alignItems: 'flex-end',
    }
  },
  view2: {
    flex: 1,
    title: {
      fontSize: 25,
      fontWeight: 'bold',
      textAlign: 'center',
      color: 'dark',
      justifyContent: 'center',
      alignItems: 'center',
    }
  },
  view3: {
    flex: 1,
    title: {
      fontSize: 15,
      fontWeight: 'bold',
      textAlign: 'center',
      color: 'dark',
    }
  },
  view4: {
    width: 360,
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    
  },
  view0: {
    flex: 1,
  },
});
