import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.background}>
        <View style={styles.containerContent}>
          <View style={styles.circle}></View>
          <Text style={styles.title}>GROW {"\n"}YOUR BUSINESS</Text>
          <Text style={styles.subtitle}>We will help you to grow your business using{"\n"}online server</Text>
          <View style={styles.buttonContainer}>
            <View style={styles.button}>
            <Button title='LOGIN' color='#E3C000' >
        </Button>
            </View>
            <View style={styles.button}>
              <Text style={styles.buttonText}>SIGN UP</Text>
            </View>
          </View>
          <Text style={styles.howWeWork}>HOW WE WORK?</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor:'#00CCF9',
  },
  background: {
    flex: 1,
    width: '100%',
    justifyContent: 'flex-start',
    alignItems: 'center',
    backgroundColor: 'linear-gradient(180deg, #C7F4F6 0%, #D1F4F6 30%, #E5F4F5 85%, #00CCF9 100%)',
  },
  containerContent: {
    alignItems: 'center',
    marginTop: 69,
  },
  circle: {
    width: 142,
    height: 142,
    borderRadius: 71,
    borderWidth: 15,
    borderColor: 'black',
    marginBottom: 20,
  },
  title: {
    textAlign: 'center',
    color: 'black',
    fontSize: 25,
    fontFamily: 'Roboto',
    fontWeight: '700',
    marginBottom: 20,
  },
  subtitle: {
    textAlign: 'center',
    color: 'black',
    fontSize: 15,
    fontFamily: 'Roboto',
    fontWeight: '700',
    marginBottom: 40,
  },
  buttonContainer: {
    flexDirection: 'row',
    marginBottom: 20,
  },
  button: {
    width: 125,
    height: 45,
    backgroundColor: '#E3C000',
    marginLeft: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },
  buttonText: {
    color: 'black',
    fontSize: 18,
    fontFamily: 'Roboto',
    fontWeight: '700',
  },
  howWeWork: {
    textAlign: 'center',
    color: 'black',
    fontSize: 18,
    fontFamily: 'Roboto',
    fontWeight: '700',
  },
});

export default App;
