import React from 'react';
import { View, Text, StyleSheet, Button } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.background}>
        <View style={styles.overlay}>
          <Text style={styles.title}>FORGET PASSWORD</Text>
          <Text style={styles.subtitle}>Provide your accountâ€™s email for which you want to reset your password</Text>
          <View style={styles.inputContainer}>
            <View style={styles.emailIcon} />
            <Text style={styles.inputLabel}>Email</Text>
          </View>
          <View style={styles.buttonContainer}>
            <View style={styles.button} />
            <Text style={styles.buttonText}>NEXT</Text>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  background: {
    width: 360,
    height: 640,
    backgroundColor: 'white',
  },
  overlay: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: 360,
    height: 640,
    backgroundColor: 'linear-gradient(180deg, #C7F4F6 0%, #D1F4F6 30%, #E5F4F5 85%, #00CCF9 100%)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  title: {
    fontSize: 25,
    fontFamily: 'Roboto',
    fontWeight: '700',
  },
  subtitle: {
    fontSize: 15,
    fontFamily: 'Roboto',
    fontWeight: '700',
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#C4C4C4',
    width: 305,
    height: 45,
  },
  emailIcon: {
    width: 22.23,
    height: 15.67,
    backgroundColor: '#011627',
  },
  inputLabel: {
    fontSize: 15,
    fontFamily: 'Roboto',
    fontWeight: '400',
    marginLeft: 10,
  },
  buttonContainer: {
    width: 305,
    height: 45,
    backgroundColor: '#E3C000',
    marginTop: 15,
  },
  button: {
    flex: 1,
    backgroundColor: 'black',
  },
  buttonText: {
    textAlign: 'center',
    color: 'black',
    fontSize: 18,
    fontFamily: 'Roboto',
    fontWeight: '700',
  },
});

export default App;
